import React, { Component } from 'react';
import EntryList from './EntryList';
import Create from './Create';

class App extends Component {
  state = {isCreate: false, isUpdate: false};

  toggleIsCreate = () => {
    this.setState({isCreate : !this.state.isCreate});
  }

  toggleIsUpdate = () => {
    this.setState({isUpdate : !this.state.isUpdate});
  }

  render() {
    console.log("Render is called")
    return(
    <div>
      <p className='topHeader'>FARM Stack sleep tracker</p>
      {this.state.isUpdate ? <p>Update</p>: null}
      {(this.state.isCreate && !this.state.isUpdate) ? <Create callBackFunction={this.toggleIsCreate}/>: null}
      {(this.state.is)}
    </div>);
  }
}

export default App;
