import {useState, useEffect} from 'react';

function Create() {
  const [newDuration, setNewDuration] = useState("");
  const [night, setNight] = useState("");
  const [currentNight, setCurrentNight] = useState("")

  useEffect(() => {
    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();
    today = yyyy + '-' + mm + '-' + dd;
    setCurrentNight(today)
  });

  function updateDuration(event) {
    event.preventDefault()
    setNewDuration(event.target.value);
    console.log(newDuration);
  }

  function updateNight(event) {
    event.preventDefault()
    setNight(event.target.value);
    console.log(night);
  }

  function submit(event) {
    event.preventDefault()

  }

  return(
    <div>
      <h1>Create a new entry</h1>
      <div className="entryListBox" 
      style={{'width':'97vw', 'alignItems':'center', 'textAlign':'center'}}>
        <form onSubmit={submit}>
          <div style={{'display':"inline-block"}}>
            <p className="bigTextSize" style={{'margin':'auto'}}> Hours you slept:</p>
          </div>
          <input value={newDuration} type="number" defaultValue='0' id='createDurationField' onChange={updateDuration} 
          style={{'fontSize': '100px', 'width':'150px', 'backgroundColor':'black', 'color': 'white', 'margin': '20px'}} min={0}></input>
          <input onChange={updateNight} type="date" id="night" name="night-selector" 
          value={currentNight}
       min="2018-01-01" max="2018-12-31">
          </input>
        </form>
      </div>
    </div>
  )
}

export default Create
