import {useState, useEffect} from 'react';
import axios from 'axios';

function Create() {
  const [duration, setDuration] = useState("");
  const [night, setNight] = useState("");
  const [defaultNight, setDefaultNight] = useState("");
  const [noDuration, setNoDuration] = useState(true);
  const [noNight, setNoNight] = useState(true);

  useEffect(() => {
    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();
    today = yyyy + '-' + mm + '-' + dd;
    setDefaultNight(today);
  }, []);

  function updateDuration(event) {
    event.preventDefault()
    if (noDuration === true && !!duration) {
      setNoDuration(false);
    }
    if (!duration) {
      setNoDuration(true);
    }
    setDuration(event.target.value);
    }

  function updateNight(event) {
    event.preventDefault();
    if (night === true && !!night) {
      setNoNight(false);
    }
    if (!night) {
      setNoNight(true);
    }
    setNight(event.target.value);
  }

  function submit(event) {
    event.preventDefault()
    axios({method: 'post',
           url:'http://127.0.0.1:8000/api/',
           data: {
            duration: duration,
            night: night
          }
        });
  }

  return(
    <div>
      <h1>Create a new entry</h1>
      <div className="entryListBox" 
      style={{'width':'97vw', 'alignItems':'center', 'textAlign':'center'}}>
        <form onSubmit={submit}>
          <div>
            <div style={{'display':"inline-block"}}>
              <p className="bigTextSize" style={{'margin':'auto'}}> Hours you slept:</p>
            </div>

            {noDuration ? <div><input value={duration} type="number" id='createDurationField' onChange={updateDuration} 
            style={{'fontSize': '100px', 'width':'150px', 'backgroundColor':'white', 'color': 'black', 'margin': '20px', 'borderColor':'red'}} min={0}></input>
            <p style={{'fontSize':'10px', 'color':'red'}}>This field is required</p>
            </div>:<input value={duration} type="number" id='createDurationField' onChange={updateDuration} 
            style={{'fontSize': '100px', 'width':'150px', 'backgroundColor':'white', 'color': 'black', 'margin': '20px'}} min={0}></input>}
          </div>
          <div style={{'display':"inline-block"}}>
            <p className="bigTextSize" style={{'margin':'auto'}}>Which night:</p>
          </div>
          <input onChange={updateNight} type="date" id="night" name="night-selector" 
          defaultValue={defaultNight} style={{'fontSize': '35px', 'width':'300px', 'margin': '20px', 'alignItems':'center', 'justifyItems':'center'}}>
          </input>
          <div>
          <input type="submit" value="Submit" style={{'fontSize':'40px'}}/>
          </div>
        </form>
      </div>
    </div>
  )
}

export default Create
