import React, { Component } from 'react';
import EntryList from './EntryList';
import Create from './Create';

class App extends Component {
  state = {isCreate: false, isUpdate: false};

  toggleIsCreate = () => {
    this.setState({isCreate : !this.state.isCreate});

  }

  setIsUpdate = (id) => {
    this.setState({isUpdate : id});
    console.log("updated")
  }

  render() {
    console.log((this.state.isUpdate));
    return(
    <div>
      <p className='topHeader'>FARM Stack sleep tracker</p>
      {(!!this.state.isUpdate && !this.state.isCreate) ? <p>Update</p>: null}
      {(this.state.isCreate && !this.state.isUpdate) ? <Create callBackFunction={this.toggleIsCreate}/>: null}
      {(!this.state.isCreate && !this.state.isUpdate) ? <EntryList updateCallBack={this.setIsUpdate} sendCreate={this.toggleIsCreate}/> : null}
    </div>);
  }
}

export default App;
